CREATE USER orderproc WITH PASSWORD 'orderproc';
CREATE DATABASE orderproc;
GRANT ALL PRIVILEGES ON DATABASE orderproc TO orderproc;

\connect orderproc;
CREATE SCHEMA truck;

GRANT ALL PRIVILEGES ON SCHEMA truck TO orderproc;

CREATE TABLE truck.driver (id integer PRIMARY KEY, id_str varchar(10), first_name varchar(100), last_name varchar(100), email varchar(50), gender varchar(10));

GRANT ALL PRIVILEGES ON TABLE truck.driver TO orderproc;

